import { combineReducers } from 'redux'

function todo(state = [], action) {
  switch(action.type) {
  default:
    return state
  }
}

function display(state = false, action) {
  switch(action.type) {
  case 'TOGGLE_DISPLAY':
    return !state
  default:
    return state
  }
}

const rootReducer = combineReducers({
  todo,
  display
})

export default rootReducer
