import { combineReducers } from 'redux'

import { SET_RESULTS } from 'actions'

function todo(state = [], action) {
  switch(action.type) {
  default:
    return state
  }
}

function display(state = false, action) {
  switch(action.type) {
  case 'TOGGLE_DISPLAY':
    return !state
  default:
    return state
  }
}

function facts(state = [], action) {
  switch(action.type) {
  case SET_RESULTS:
    return action.results
  default:
    return state
  }
}

const rootReducer = combineReducers({
  todo,
  facts,
  display
})

export default rootReducer
